# patch the fieldAliases to include the iso standards
# iso19115 iso19139 iso19115p2
tmpDict = {}
keys = fieldAliases.keys()
for k in keys:
    if k.startswith("sans1878"):
        val = fieldAliases[k]
        new19115 = k.replace("sans1878","iso19115")
        new19139 = k.replace("sans1878","iso19139")
        new19115p2 = k.replace("sans1878","iso19115p2")
        tmpDict[new19115] = val
        tmpDict[new19139] = val
        tmpDict[new19115p2] = val
fieldAliases.update(tmpDict)

